mapper.insert:
		INSERT INTO SPORT_USER (useridx, email, nickname, password, phone, gender, image, image_pi)
		VALUES (SEQ_USERIDX.NEXTVAL, #{email}, #{nickname}, #{password}, #{phone}, #{gender},


                #{image}, #{image_pi}


                'https://res.cloudinary.com/djlee4yl2/image/upload/w_1000,c_fill,ar_1:1,g_auto,r_max,bo_5px_solid_red,b_rgb:262c35/v1713834954/logo/github_logo_icon_tpisfg.png', NULL


		)

mapper.select:
SELECT COUNT(*) AS CNT FROM FREE_BBS
UPDATE SPORT_USER SET nickname = '${nickname}', password = '${password}', phone = '${phone}' WHERE email = '${email}'
UPDATE SPORT_USER SET image = '${image}', image_pi = '${imagePi}' WHERE email = '${email}'
SELECT * FROM FREE_BBS WHERE USERIDX = ( SELECT USERIDX FROM SPORT_USER WHERE EMAIL = '${email}' )
SELECT EMAIL, NICKNAME, PHONE, GENDER, IMAGE, IMAGE_PI FROM SPORT_USER WHERE EMAIL = '${email}'
SELECT * FROM FREE_BBS
SELECT * FROM(SELECT ROWNUM R_NUM, F.* FROM (SELECT B.BBS_IDX, B.USERIDX, U.NICKNAME, B.SUBJECT, B.CONTENT, B.WRITE_DATE, B.IMAGE FROM FREE_BBS B, SPORT_USER U WHERE B.USERIDX = U.USERIDX ORDER BY BBS_IDX DESC) F WHERE ROWNUM <= #{end}) WHERE R_NUM >= #{begin}
INSERT INTO FREE_BBS (BBS_IDX, USERIDX, SUBJECT, CONTENT, WRITE_DATE) VALUES (SEQ_FREE_BBS_IDX.NEXTVAL, #{useridx}, #{subject}, #{content}, SYSDATE)
SELECT B.BBS_IDX, B.USERIDX, U.NICKNAME, B.SUBJECT, B.CONTENT, B.WRITE_DATE, B.IMAGE FROM FREE_BBS B, SPORT_USER U WHERE B.USERIDX = U.USERIDX AND B.BBS_IDX = #{bbsIdx}
UPDATE FREE_BBS SET SUBJECT = '${subject}', CONTENT = '${content}' WHERE BBS_IDX = ${bbsIdx}
DELETE FROM FREE_BBS WHERE BBS_IDX = #{bbsIdx}
SELECT C.COMMENT_IDX, C.USERIDX, U.NICKNAME, C.BBS_IDX, C.CONTENT, C.WRITE_DATE FROM FREE_COMMENTS C, SPORT_USER U WHERE C.USERIDX = U.USERIDX AND C.BBS_IDX = #{bbsIdx}
SELECT * FROM FREE_COMMENTS WHERE COMMENT_IDX = #{commentIdx}
INSERT INTO FREE_COMMENTS (COMMENT_IDX, USERIDX, BBS_IDX, CONTENT, WRITE_DATE) VALUES (SEQ_FREE_COMMENT_IDX.NEXTVAL, #{useridx}, #{bbsIdx}, #{content}, SYSDATE)
UPDATE FREE_COMMENTS SET CONTENT = #{content} WHERE COMMENT_IDX = #{commentIdx}
DELETE FROM FREE_COMMENTS WHERE COMMENT_IDX = #{commentIdx}
